import React, { useState } from 'react';
import axios from 'axios';

const CreatePhoto = ({ onAddPhoto }) => {
  // Estado local para los datos del formulario
  const [formData, setFormData] = useState({
    photoFile: null, // El archivo de imagen seleccionado
    title: '',
    description: '',
    date: '',
  });

  // Manejar cambios en los campos del formulario, incluido el archivo
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'photoFile') {
      // Si es el campo de archivo, guarda el archivo seleccionado en el estado
      setFormData({
        ...formData,
        [name]: files[0], // Solo permite seleccionar un archivo
      });
    } else {
      // Para otros campos, maneja los cambios como antes
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  // Enviar el formulario al servidor
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Crea un objeto FormData para enviar el archivo
      const formDataToSend = new FormData();
      formDataToSend.append('photo', formData.photoFile);
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('date', formData.date);

      // Enviar los datos al servidor utilizando axios.post
      const response = await axios.post('http://localhost:8000/photos', formDataToSend);

      // Si el envío es exitoso, deberías recibir una respuesta del servidor.
      // Puedes manejar la respuesta aquí según tus necesidades.
      if (response.status === 201) {
        // Realiza las acciones necesarias, como limpiar el formulario.
        setFormData({
          photoFile: null,
          title: '',
          description: '',
          date: '',
        });

        console.log('Foto creada con éxito:', response.data);

        // Llama a la función onAddPhoto para actualizar la lista de fotos en ShowPhotos
        onAddPhoto(response.data); // Asume que response.data contiene la nueva foto
      }
    } catch (error) {
      console.error('Error al agregar la foto: ', error);
    }
  };

  return (
    <div>
      <h2>Agregar Nueva Foto</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="photoFile">Cargar Foto:</label>
          <input
            type="file"
            id="photoFile"
            name="photoFile"
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="title">Título:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="description">Descripción:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <div>
          <label htmlFor="date">Fecha:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Agregar Foto</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
