import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const CreatePhoto = () => {
  const [formData, setFormData] = useState({
    photo: '',
    title: '',
    description: '',
    date: '',
  });

  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Enviar los datos al servidor utilizando axios.post
      await axios.post('http://localhost/3000', formData);

      // Redirigir al usuario a la página de visualización de fotos
      history.push('/show-photos');
    } catch (error) {
      console.error('Error creando foto: ', error);
    }
  };

  return (
    <div>
      <h2>Crear Nueva Foto</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="photo">URL de la Foto:</label>
          <input
            type="text"
            id="photo"
            name="photo"
            value={formData.photo}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="title">Título:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="description">Descripción:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <div>
          <label htmlFor="date">Fecha:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Crear Foto</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
