import React, { useState } from 'react';
import axios from 'axios';

const CreatePhoto = ({ onAddPhoto }) => {
  const [formData, setFormData] = useState({
    photo: null, // Use null to represent no image initially
    title: '',
    description: '',
    date: '',
  });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;

    if (type === 'file') {
      setFormData({
        ...formData,
        [name]: files[0],
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSubmit = new FormData();
    formDataToSubmit.append('photo', formData.photo);
    formDataToSubmit.append('title', formData.title);
    formDataToSubmit.append('description', formData.description);
    formDataToSubmit.append('date', formData.date);

    const port = 8000;
    const URI = `http://localhost:${port}/photos`;

    try {
      const response = await axios.post(URI, formDataToSubmit);

      if (response.status === 201) {
        setFormData({
          photo: null,
          title: '',
          description: '',
          date: '',
        });

        console.log('Photo created successfully:', response.data);

        onAddPhoto(response.data);
      }
    } catch (error) {
      console.error('Error adding the photo: ', error);
    }
  };

  return (
    <div>
      <h2>Add New Photo</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="photo">Photo:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <div>
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Add Photo</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
