import React, { useState } from 'react';
import axios from 'axios';

const CreatePhoto = ({ onAddPhoto }) => {
  // Local state for form data, including a new 'photoFile' field for file input
  const [formData, setFormData] = useState({
    photoFile: null, // This will hold the selected image file
    title: '',
    description: '',
    date: '',
  });

  // Handle changes in the form fields, including the file input
  const handleChange = (e) => {
    if (e.target.name === 'photoFile') {
      // If the input is a file input, update 'photoFile' with the selected file
      setFormData({
        ...formData,
        [e.target.name]: e.target.files[0], // Store the selected file
      });
    } else {
      // For other fields, update the form data as usual
      const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  // Submit the form data to the server, including the selected file
  const handleSubmit = async (e) => {
    e.preventDefault();

    const port = 8000;
    const URI = `http://localhost:${port}/photos`;

    try {
      const formDataForUpload = new FormData(); // Create a FormData object

      // Append the file and other form data to the FormData object
      formDataForUpload.append('photo', formData.photoFile); // 'photo' should match the name of the field expected on the server
      formDataForUpload.append('title', formData.title);
      formDataForUpload.append('description', formData.description);
      formDataForUpload.append('date', formData.date);

      // Send data to the server using axios.post with FormData
      const response = await axios.post(URI, formDataForUpload, {
        headers: {
          'Content-Type': 'multipart/form-data', // Important: Set the content type to multipart/form-data
        },
      });

      // If the submission is successful, should receive a response from the server.
      if (response.status === 201) {
        // Perform necessary actions, like clearing the form.
        setFormData({
          photoFile: null, // Clear the selected file
          title: '',
          description: '',
          date: '',
        });

        console.log('Photo created successfully:', response.data);

        // Call the onAddPhoto function to update the list of photos in ShowPhotos
        onAddPhoto(response.data); // Assumes that response.data contains the new photo
      }
    } catch (error) {
      console.error('Error adding the photo: ', error);
    }
  };

  return (
    <div>
      <h2>Add New Photo</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="photo">Photo:</label>
          <input
            type="file"
            id="photo"
            name="photoFile"
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <div>
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Add Photo</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
