import React, { useState } from 'react';
import axios from 'axios';

const CreatePhoto = ({ onAddPhoto }) => {
  // Local state for form data
  const [formData, setFormData] = useState({
    photo: null, // Use null instead of a string to store the file
    title: '',
    description: '',
    date: '',
  });

  // Handle changes in the form fields, including the file input
  const handleChange = (e) => {
    const { name, value, files } = e.target;

    // If it's a file input field, update 'photo' with the selected file
    if (name === 'photo') {
      setFormData({
        ...formData,
        photo: files[0], // Store the first selected file
      });

      // Log the form data here when a file is selected
      console.log('Form Data when Photo is Selected:', {
        ...formData,
        photo: files[0].name, // Log the name of the selected file
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  // Submit the form data to the server
  const handleSubmit = async (e) => {
    e.preventDefault();

    const port = 8000;
    const URI = `http://localhost:${port}/photos`;

    try {
      // Create a FormData object to send multipart/form-data (including the file)
      const formDataToSend = new FormData();
      formDataToSend.append('photo', formData.photo); // Add the file to the form

      // Add other fields to the form
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('date', formData.date);

      // Send the form data with axios.post
      const response = await axios.post(URI, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data', // Specify the content type
        },
      });

      // If the upload is successful, we should receive a response from the server
      if (response.status === 201) {
        // Perform necessary actions, like clearing the form
        setFormData({
          photo: null, // Reset the file input
          title: '',
          description: '',
          date: '',
        });

        console.log('Photo uploaded successfully:', response.data);

        // Call the onAddPhoto function to update the list of photos in ShowPhotos
        onAddPhoto(response.data); // Assuming that response.data contains the new photo
      }
    } catch (error) {
      console.error('Error uploading the photo: ', error);
    }
  };

  return (
    <div>
      <h2>Upload New Photo</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label htmlFor="photo">Photo:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="form-control"
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload Photo</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
