import React, { useState } from 'react';
import axios from 'axios';

const CreatePhoto = ({ onAddPhoto }) => {
  const [formData, setFormData] = useState({
    photo: null,
    title: '',
    description: '',
    date: '',
  });

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      photo: e.target.files[0], // Guarda el archivo seleccionado
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append('photo', formData.photo); // Utiliza el estado 'formData'
    formDataToSend.append('title', formData.title);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('date', formData.date);

    try {
      const response = await axios.post('http://localhost:8000/photos', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data', // Asegúrate de establecer el tipo de contenido como 'multipart/form-data'
        },
      });

      if (response.status === 201) {
        setFormData({
          photo: null,
          title: '',
          description: '',
          date: '',
        });

        console.log('Photo created successfully:', response.data);

        onAddPhoto(response.data);
      }
    } catch (error) {
      console.error('Error adding the photo: ', error);
    }
  };

  return (
    <div>
      <h2>Add New Photo</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="photo" className="form-label">Photo:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            onChange={(e) => handleFileChange(e)}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="form-control"
          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="date" className="form-label">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={(e) => setFormData({ ...formData, date: e.target.value })}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">Add Photo</button>
      </form>
    </div>
  );
};

export default CreatePhoto;
