const CreatePhoto = ({ onAddPhoto }) => {
  const [formData, setFormData] = useState({
    photo: null,
    title: '',
    description: '',
    date: '',
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === 'photo') {
      // Store the selected file in the state
      setFormData({
        ...formData,
        photo: files[0],
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const port = 8000;
    const URI = `http://localhost:${port}/photos`;

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('photo', formData.photo);
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('date', formData.date);

      const response = await axios.post(URI, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 201) {
        setFormData({
          photo: null,
          title: '',
          description: '',
          date: '',
        });

        console.log('Photo uploaded successfully:', response.data);
        onAddPhoto(response.data);
      }
    } catch (error) {
      console.error('Error uploading the photo: ', error);
    }
  };

  return (
    <div>
      <h2>Upload New Photo</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="form-group">
          <label htmlFor="photo">Photo:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="form-control"
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload Photo</button>
      </form>
    </div>
  );
};
